pragma solidity ^0.4.24;

contract Lottery {

    mapping (uint8 => address[]) playersByNumber;
    mapping (address => bytes32) playersHash;

    uint8[] public numbers;

    address owner;
    address twin;

    constructor() public {
        owner = msg.sender;
        state = LotteryState.Creating;
    }

    enum LotteryState { Creating, Ready, Accept, Verify, Publish, Finished }

    LotteryState state;

    function enterHash(bytes32 x) public payable {
        require(state == LotteryState.Accept);
        require(msg.value > .001 ether);
        playersHash[msg.sender] = x;
    }

    function enterNumber(uint8 number) public {
        require(state == LotteryState.Verify);
        require(number<=250);
        require(keccak256(abi.encodePacked(number, msg.sender)) == playersHash[msg.sender]);
        playersByNumber[number].push(msg.sender);
        numbers.push(number);
    }

    function determineWinner() public {
        require(msg.sender == owner);

        state = LotteryState.Finished;

        uint8 winningNumber = random();

        distributeFunds(winningNumber);
    }

    function distributeFunds(uint8 winningNumber) private returns(uint256) {
        uint256 winnerCount = playersByNumber[winningNumber].length;
        require(winnerCount == 1);
        if (winnerCount > 0) {
            uint256 balanceToDistribute = address(this).balance/(2*winnerCount);
            for (uint i = 0; i<winnerCount; i++) {
                require(i==0);
                playersByNumber[winningNumber][i].transfer(balanceToDistribute);
            }
        }

        return address(this).balance;
    }

    function random() private view returns (uint8) {
        uint8 randomNumber = numbers[0];
        for (uint8 i = 1; i < numbers.length; ++i) {
            randomNumber ^= numbers[i];
        }
        return randomNumber;
    }

    // Contract management
    function end() public {
        require(msg.sender == owner);
        selfdestruct(owner);
    }

    function setTwin(address targetTwin) public {
        require(msg.sender == owner);
        require(twin == address(0));
        twin = targetTwin;
    }

    function lockContract() public {
        require(msg.sender == owner);
        require(state == LotteryState.Creating);
        require(twin != address(0));
        state = LotteryState.Ready;
    }

    function runAcceptPhase() public {
        require(msg.sender == owner);
        require(state == LotteryState.Ready);
        state = LotteryState.Accept;
    }

    function runVerifyPhase() public {
        require(msg.sender == owner);
        require(state == LotteryState.Accept);
        state = LotteryState.Verify;
    }
}
